# Problem 8: Largest product in a series

library(EulerFunctions)


number = ""

index <- 1
greatest <- 0

# 988 since the last adjacent numbers will be in indexes 988 to 1000, both included.
while (index <= 988) {
    # Extract substring from "index" to "index + 12" (13 chars)
    adjacent <- substr(number, index, index + 12) 
    adjacent <- number_to_vector(adjacent) # vector of separated digits
    
    if (!any(adjacent == 0)) { # no zero found in substring 
        if (prod(adjacent) > greatest) { 
            greatest <- prod(adjacent) # assing new value as greatest
        }
        
        index <- index + 1
        
    } else { # skip to next number after last "0" in substring
        n <- max(which(adjacent == "0"))
        index <- index + n
    }
}


# Answer
greatest
